package assignment;

/*
 * 	W.A.J.P in Java to display the first 10 natural numbers using while loop.
 */
public class Que2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		while (i<=10) {
			System.out.println(i);
			i++;
		}
	}

}
