package com.basic;

//import java.util.Iterator;

public class LoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//loops 
		
		for (int i = 1; i <=5; i++) {
			System.out.println(i);
		}
		
		int j=6;
		while (j<=10) {
			System.out.println(j);
			j++;
		}
		
		int k = 11;
		do {
			System.out.println(k);
			k++;
		} while (k<=15);
	}

}
